    /**
     * Updates information about the currently selected Report
     */
    public void updateInformationDisplay() {
        if(selected != null) {
            ReportTitleLabel.setText(selected.getTitle());
            TitleLabel.setText(selected.getTitle());
            DescriptionArea.setText(selected.getDescription());
        }
    }

    /**
     * Populates the Selection of Reports available to the user
     */
    private void populateReportsComboBox() {
        for(String title : theReports.getReportTitles()) {
            ReportComboBox.addItem(title);
        }
    }

    /**
     * Removes the current table and replaces it with a new one
     * @param table the new table
     */
    private void addTable(final JTable table) {
        ReportScrollPane.getViewport().remove(ReportTable);
        ReportTable = table;
        ReportScrollPane.getViewport().add(ReportTable);
        revalidate();
        repaint();
    }

