    /**
     * Queries the database using the given selected report
     * @param evt the buttonclick event
     */
    private void GetReportButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_GetReportButtonActionPerformed
        updateInformationDisplay();
        JTable testTable = null;
        try {
            testTable = dbm.executeQueryGetTable(selected.getQuery());
            testTable.setAutoCreateRowSorter(true);
        } catch (SQLException ex) {
            Logger.getLogger(ReportsPanel.class.getName()).log(Level.SEVERE, null, ex);
        }
        if (testTable != null) {
            addTable(testTable);
        }
    }//GEN-LAST:event_GetReportButtonActionPerformed


